import React, {useState} from "react";
import List from "./List.jsx"

function App() {

  const [newItem, setNewItem] = useState("");

  function handleClick(event){
    setNewItem("hello!");    
  }


  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input type="text" />
        <button onClick={handleClick}>
          <span>Add</span>
        </button>
      </div>
      <div>
        <ul>
         <List item="newItem"/> 
        </ul>
      </div>
    </div>
  );
}

export default App;
